                {connectionMethod === "qr" &&
                  "Scan the QR code with your mobile wallet"}
                {connectionMethod === "hardware" &&
                  "Connect your hardware wallet via USB"}
                {connectionMethod === "bluetooth" &&
                  "Pair your wallet via Bluetooth"}
